html:
{% set cgst = {} %}
{% set sgst = {} %}
{% set igst = {} %}
<style>
    th{
        border:1.5px solid black;
        text-align:center; 
        line-height: 1.1;
    }
    .td1{
        border-left:1.5px solid black; 
        line-height: 1.1;
    }
    .td2{
        border:1.5px solid black; 
        line-height: 1.1;
        text-align:right;
        font-weight:bold;
    }
    .center{
        text-align:center;
    }
</style>

{% if letter_head and not no_letterhead -%}
{% if print_settings.repeat_header_footer %}
   <br> <div class="letter-head">{{ letter_head }}</div>
{%- endif %}
{%- endif %}

<table style="width:100%;font-size:9px;">
    <tr style="border-bottom:1.5px solid black;font-size:9px;">
        <th style="width:4%">
            No.
        </th>
        <th style="width:22%">
            Part No.
        </th>
        <th style="width:32%">
            Description
        </th>
        <th style="width:5%">
            Qty
        </th>
        <th style="width:7%">
            Unit
        </th>
        <th style="width:13%">
            Unit Price INR
        </th>
        <th style="width:7%">
            Tax
        </th>
        <th style="width:10%">
            Total INR
        </th>
    </tr>
    {% set serial_no=[] %}
    {% for item in doc.items %}
    {% set items= frappe.get_doc('Item',item.item_code) %}
    <tr>
         {% set _=serial_no.append(1) %}
        <td class="td1 center">
            {{ serial_no|length }}
        </td>
        <td class="td1">
            {% if items.part_no %} {{ items.part_no}} {% endif %}
        </td>
        <td class="td1">
            {{ items.name }}
        </td>
        <td class="td1 center">
            {{ item.qty|int }}
        </td>
        <td class="td1 center">
           {% if item.uom %} {{ item.uom }} {% else %} <span style="color:red;">No<br>Units</span> {% endif %}
        </td>
        <td class="td1 center">
            {{ item.price_list_rate }}
        </td>
        <td class="td1 center">
            {{ '%.0f' % tax_val|sum }}%
        </td>
        <td class="td1 center" style="border-right:1.5px solid black;">
            {{ item.amount }}
        </td>
    </tr>
    {% if tax_val|length == 2 %}
        {% set taxx = tax_val[0] %}
        {% set amount = ((tax_val[0]/100) * item.amount) %}
        {% set amount1 = ((tax_val[1]/100) * item.amount) %}
        {% if taxx in cgst %}
            {% set x=[] %}
            {% set _=x.append(cgst[taxx]) %}
            {% set _= cgst.update({taxx: x[0]+ amount})%}
        {% else %}
        {% set _=cgst.update({taxx: amount}) %}
        {% endif %}
        {% set taxx = tax_val[1] %}
        {% if taxx in sgst %}
            {% set x=[] %}
            {% set _=x.append(sgst[taxx]) %}
            {% set _= sgst.update({taxx: x[0]+ amount1})%}
        {% else %}
        {% set _=sgst.update({taxx: amount1}) %}
        {% endif %}
     {% elif tax_val|length == 1 %}
        {% set taxx = tax_val[0] %}
        {% set amount = ((tax_val[0]/100) * item.amount) %}
        {% if taxx in igst %}
            {% set x=[] %}
            {% set _=x.append(igst[taxx]) %}
            {% set _= igst.update({taxx: x[0]+ amount})%}
        {% else %}
        {% set _=igst.update({taxx: amount}) %}
        {% endif %}
    {% endif %}
    {% endfor %}
    <tr>
        <td class="td2">

        </td>
        <td class="td2" colspan='6'>
            {{ "Subtotal" }}
        </td>
        <td class="td2 center">
            {{ doc.total }}
        </td>
    </tr>

    {% for i in range (cgst|length) %}
    <tr>
        <td class="td2">

        </td>
        <td class="td2" colspan='6'>
            {{ "(+) "}}{{'%0.0f' % ((cgst.keys()|list))[i]|int  }}{{"% CGST" }}
        </td>
        <td class="td2 center">
            {{ ((cgst.values()|list))[i] }}
        </td>
    </tr>
    <tr>
        <td class="td2">

        </td>
        <td class="td2" colspan='6'>
            {{ "(+) "}}{{'%0.0f' % ((sgst.keys()|list))[i]|int}}{{"% SGST" }}
        </td>
        <td class="td2 center">
            {{ ((sgst.values()|list))[i] }}
        </td>
    </tr>
    {% endfor %}
    {% for i in igst %}
    <tr>
        <td class="td2">

        </td>
        <td class="td2" colspan='6'>
            {{ "(+) "}}{{'%0.0f' % i}}{{"% iGST" }}
        </td>
        <td class="td2 center">
            {{ igst[i] }}
        </td>
    </tr>
     {% endfor %}
    <tr style="height:2px!important;">

    </tr>
    <tr>
        <td class="td2">

        </td>
        <td style="border-top:1px solid black;border-bottom:1px solid black;border-left:1px solid black;" colspan="2">

        </td>
        <td style="border-top:1px solid black;border-bottom:1px solid black;" class="center">
           {{doc.total_qty|int}} 
        </td>

        <td  style="text-align:right;border-top:1px solid black;border-bottom:1px solid black;border-right:1px solid black;" colspan='3'>
            {{ "Total in INR (Rounded):" }}
        </td>
        <td class="td2 center">
            {{ doc.rounded_total }}
        </td>
    </tr>
</table>

<div>
<div="terms">
<br>
<b><u>Terms and Conditions</u><br></b>
<span style="font-size:10px;">{{doc.terms}}</span>

<dic class="bank">
<p style="padding-left:60px;font-size:10px; line-height: 1.2;"><br>
{% set bank = frappe.get_doc("Bank Account", "Sri Kumaran SuperStore - ICICI")%}
<b><u>Please remit payments to:</u><br></b>
<b>Name :</b>{{bank.account_name}}<br>
<b>Bank Name :</b>{{bank.bank}}<br>
<b>Account No :</b>{{bank.bank_account_no}}<br>
<b>IFSC Code :</b>{{bank.branch_code}}<br>
<b>Branch :</b>{% if bank.branch %}{{bank.branch}}{% endif %}<br></p>

</div>

<div class=sign>
<p style="line-height: 1.1;margin-top:10px;font-size:10px; width:100%;">
<b>
For and on behalf of <br>
{{doc.company}}
<BR>
<BR>
<BR>
<BR>
<br>
________________________<br>
Authorised Signature</b></p>
</div>
