css:
.page{
    overflow:hidden;
    width:100%;
}

.print-format {
    margin-left: 5mm;
    margin-right: 5mm;
    margin-top: 10mm;
}


.print-format td{
    padding: 0px!important;
    border-spacing: 0!important;
    margin-left:5mm; 
    margin-right:5mm;
    margin-top:3mm;
    margin-bottom:5mm;
    height:0!important;
    padding-left: 2px!important;
}
* {
  box-sizing: border-box;
}

/* Create three unequal columns that floats next to each other */
.column {
  float: left;
}
.left {
  width: 73%!important;
}
.right {
  width: 27%!important;
  border-left:0.5px solid black;
}
table .t1{
  width: 100%;
  border: 1px solid;
  font-size:10px!important;
}

.t1 td{
    width:25%;

    padding: 0!important; border-spacing: 0!important;  margin:0!important;
}
.tl{
    border-left:1px solid black;

}
.tr{
    border-right:1px solid black;

}

.itemth{

    border-top: 1px solid black;
    border-left: 1px solid black;
    border-bottom: 1px solid black;
    text-align:center;
}
.itemtd{
    border-left: 1px solid black;
    padding: 0px!important; border-spacing: 0!important;  margin:0!important; height:0!important;padding-left: 2px!important;
}
.page{
    border: 2px solid black;
    width: 100%;
}

.invoiceaddress{
    width:100%;
    overflow:hidden;
}

.itemth1{
    border-bottom: 1px solid black;
    text-align:center;
}

.letter-head{
    margin-top:-30px;
}
.table4 td{
    border-top:1px solid black;

    border-bottom: 1px solid black;
    line-height:13px;
}

.table4 th{
    text-align:center;
}

.table3{
    width:100%;
    overflow: hidden;

}
.table3 table{
    width:100%;
    overflow: hidden;
    font-size:14px

}

.table3 tr{
    border-right:1px solid black;
}

.table3 th{
    border-top:1px solid black;
    border-left:1px solid black;
    line-height:13px;
    text-align:center;
}

.table3 td{
    border-left:1px solid black;
    line-height:13px;
}
