css:
.print-format {
  margin-top: 6mm;
  margin-left: 7mm;
  margin-right: 7mm;
  margin-bottom: 13mm;
}
.print-format td{
    height: 0mm !important;
    padding: 2px !important;
}

.master{
    font-family:arial;
}

.page{
    border: 1px solid black;
    width: 100%;
    height: 986px;
}
.split{
    top: 5mm;
    width: 47%;
    position: relative;
}
.td{
    border: 1px solid black;
    border-collapse: border-collapse;
    height: 22px;
}

.itemtable{

    top: -2mm;
    position: relative;
    width: 100%;
    border-bottom: 1px solid black;
}
.itemth{
    height: 10px;
    border-top: 1px solid black;
    border-left: 1px solid black;
}
.itemtd{
    border-left: 1px solid black;



}
.taxtable{
    width: 100%;
}
.taxtd{
    border: 1px solid black;
}
.taxtd1{
    border: 1px solid black;
    border-bottom: 0;
}
.taxtd2{
    border: 1px solid black;
    border-top: 0;
    text-align: right;
}
.outstdet{
    width: 100px;
    position: relative;
    text-align: right;
}
.pandec{
    width: 100%;
    padding: 5px;
    border-bottom: 1px solid black;
}
.row {

  display: flex;
  margin-left:-5px;
  margin-right:-5px;

}
.row1 {
  position: relative;
  top:-1.9mm;
  display: flex;
  margin-left:-5px;
  margin-right:-5px;

}

.column {
  float: left;
  width: 50%;
  /*padding-left: 5px;*/
  padding-right: 6px;
}

.column4 {
  float: left;
  width: 50%;
  padding-left: 5px;
  padding-right: 5px;
}

.column1 {
  float: left;
  width: 53%;
  padding-left: 5px;
  padding-right: 5px;
}
.column2 {
  float: left;
  width: 44%;
  padding-left: 5px;
  padding-right: 5px;
}
.column3{
  float: left;
  width: 100%;
  padding-left: 5px;
  padding-right: 5px;
}
.td {
  height: 200px;
}
.textright {
  text-align: right;
}
